import React, { Component } from 'react';
import { Environment } from '../../../src/components/environment';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import Header from '../../../src/layouts/header';
import TodayDeal from '../../../src/layouts/todays_deal';
import Footer from '../../../src/layouts/footer';
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import OwlCarousel from 'react-owl-carousel2';
import Alert from 'react-s-alert';

class ProductDetail extends Component{
    constructor (props) {
        super(props);
        this.state = {
            getProductDetail: [],
            quantity : 0,
            user_id_to_send :'',
            wishlistStatus : '',
        }
        var user_id_send = localStorage.getItem("user_id")?localStorage.getItem("user_id"):"";
        let id = this.props.match.params.id;
        this.addQuantity = this.addQuantity.bind(this);
        this.removeQuantity = this.removeQuantity.bind(this);
        this.addProductIntoCart = this.addProductIntoCart.bind(this);
        this.addProductIntoWishlist = this.addProductIntoWishlist.bind(this);
        this.setState({'user_id_send': user_id_send});

        axios.get(Environment.apiurl+'/product/details/'+id,{user_id:localStorage.getItem("user_id")})
        .then(result => {
            if(result){
                this.setState({getProductDetail: result.data.response});
                this.setState({'id': this.props.match.params.id});
            }
        });
    }



    addQuantity(e){
        this.setState({ quantity: this.state.quantity+1 });
    }
    removeQuantity(e){
        if(this.state.quantity>0){
            this.setState({ quantity: this.state.quantity-1 });
        }
    }

    addProductIntoCart(param, param1, param2, param3, param4) {
        var quantity = this.state.quantity;
        if(quantity>0){
            var cartData =
            {
                "product_id":param,
                "category_id":param1,
                "sub_category_id":param2,
                "price":param3,
                "user_id":param4,
                "quantity":quantity,
            };

            axios.post(Environment.apiurl+"/addProductIntoCart", cartData).then(result => {
                if(result.data.status=="success"){
                    this.setState({ quantity: 0 });
                    // console.log(result.data.status);
                    Alert.success(result.data.message, {
                        position: 'bottom-right',
                        timeout: 3000
                    });
                }else{
                    Alert.error(result.data.message, {
                        position: 'bottom-right',
                        timeout: 3000
                    });
                }
            });
        } else {
            Alert.error("Quantity should be grater than 0.", {
                position: 'bottom-right',
                timeout: 3000
            });
        }

    }

    addProductIntoWishlist(param, param1) {
        var wishlistData =
        {
            "product_id":param,
            "user_id":param1,
        };

        axios.post(Environment.apiurl+"/addProductIntoWishlist", wishlistData).then(result => {
            this.setState({wishlistStatus : result.data.status});
            if(result.data.status=="success"){
                Alert.success(result.data.message, {
                    position: 'bottom-right',
                    timeout: 3000
                });
            }else{
                Alert.error(result.data.message, {
                    position: 'bottom-right',
                    timeout: 3000
                });
            }
        });
    }



    render() {
        const options1 = {
            items: 1,
            // dots:false,
            callbacks:true,
            // nav:true,
            loop:true,
            autoplay: true,
            autoplayTimeout:3500,
            smartSpeed:800,
            singleItem:true,
            // navText: ["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"],
            responsiveClass:true,    
        };

        const options2 = {
            margin:10,
            items: 4,
            dots:false,
            callbacks:true,
            // nav:true,
            loop:true,
            autoplay: true,
            autoplayTimeout:3500,
            smartSpeed:800,
            singleItem:true,
            // navText: ["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"],
            responsiveClass:true,    
        };
        // console.log(this.state.wishlistStatus);
        return (
            <div>
                <Header />

                <section class="pageBanner">
                    <figure><img src={Environment.apiurl+"/images/page-banner.jpg"} class="img-fluid" /></figure>
                    <div class="overLay_1">
                        <div class="container">
                            <div class="row">
                                <div class="col-sm-12 text-center">
                                    <h1>Shop</h1>
                                    <ol class="breadcrumb">
                                        <li><a href={Environment.webrl}>Home</a></li>
                                        <li class="active">Product Details</li>
                                    </ol>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

                <If condition={this.state.getProductDetail.length > 0}>
                    <Then>
                    {this.state.getProductDetail.map((item, index) =>    
                    <div>                
                    <section class="listing">
                        <div class="container">
                            <div class="row">
                            <div class="col-md-5 thumSlider">

                            <OwlCarousel id="sync1" class="owl-carousel owl-theme"  options={options1}>
                                    <div class="item"><img src={Environment.apiurl+"/images/big1.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big2.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big3.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big4.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big1.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big2.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big3.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big4.jpg"} class="img-fluid" /></div>
                                </OwlCarousel>
                                <OwlCarousel id="sync2" class="owl-carousel owl-theme"  options={options2}>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb1.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb2.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb3.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb4.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb1.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb2.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb3.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/thumb4.jpg"} class="img-fluid" /></div>
                            </OwlCarousel>

                            </div>
                            <div class="col-md-7 detailDis">
                                <h2>{item.product_name}</h2>
                                <p class="price">${item.price} </p>

                                <If condition={item.quantity > 0}>
                                    <Then>
                                        <p class="stock">In stock</p>
                                    </Then>
                                    <Else>
                                        <p class="stock">Out of stock</p>
                                    </Else>
                                </If>
                                <p>{ ReactHtmlParser(item.description) }</p>
                                <div class="plusMinus">
                                <form id='myform' method='POST' action='#'>
                                    <input type='button' value='-' class='numb' field='quantity' onClick={(e) => this.removeQuantity()}/>
                                    <input type='text' name='quantity' id="quantity" value={this.state.quantity} class='qty numb' />
                                    <input type='button' value='+' class='numb' field='quantity' onClick={(e) => this.addQuantity()}/>
                                </form>
                                <button class="cartBtn" onClick={(e) => this.addProductIntoCart(item.id, item.category_id, item.sub_category_id, item.price, localStorage.getItem("user_id"))}>Add to Cart</button>
                                </div>
                                <ul class="otherLinks">

                                <If condition={this.state.wishlistStatus}>
                                <Then>
                                    <If condition={this.state.wishlistStatus === 'success'}>
                                        <Then>
                                            <li><a href="javaScript:void(0);"onClick={(e) => this.addProductIntoWishlist(item.id, localStorage.getItem("user_id"))}><i class="far fa-heart"></i></a></li>
                                        </Then>
                                        <Else>
                                            <li><a href="javaScript:void(0);" onClick={(e) => this.addProductIntoWishlist(item.id, localStorage.getItem("user_id"))}><i class="far fa-heart"></i></a></li>
                                        </Else>
                                    </If>
                                </Then>
                                <Else>
                                    <If condition={item.favorite > 0}>
                                        <li><a href="javaScript:void(0);"onClick={(e) => this.addProductIntoWishlist(item.id, localStorage.getItem("user_id"))}><i class="far fa-heart"></i></a></li>
                                    </If>
                                    <If condition={item.favorite == 0}>
                                        <li><a href="javaScript:void(0);" onClick={(e) => this.addProductIntoWishlist(item.id, localStorage.getItem("user_id"))}><i class="far fa-heart"></i></a></li>
                                    </If>
                                </Else>
                                </If>


                                <li><a href="#"><i class="fas fa-exchange-alt"></i></a></li>
                                <li><a href="#"><i class="fas fa-print"></i></a></li>
                                </ul>
                            </div>
                            </div>
                        </div>
                    </section>

                    <section class="myTab">
                        <div class="container">
                            <div class="row">
                                <div class="col-md-12">
                                    <div id="parentHorizontalTab">
                                        <ul class="resp-tabs-list hor_1">
                                            <li>Description</li>
                                            <li>REVIEWS</li>
                                            <li>Product Tags</li>
                                        </ul>
                                        <div class="resp-tabs-container hor_1">
                                            <div>
                                            { ReactHtmlParser(item.description) }
                                            </div>
                                            <div>
                                            { ReactHtmlParser(item.description) }
                                            </div>
                                            <div>
                                            { ReactHtmlParser(item.description) }
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                    </div>
                    )}
                    </Then>
                </If>

                <TodayDeal />
                <Footer />
            </div>
        )
    }    

}

export default ProductDetail;
