import React, { Component } from 'react';
import { Environment } from '../../../src/components/environment';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import Header from '../../../src/layouts/header';
import TodayDeal from '../../../src/layouts/todays_deal';
import Footer from '../../../src/layouts/footer';
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import OwlCarousel from 'react-owl-carousel2';
import Alert from 'react-s-alert';

class ProductDetail extends Component{
    constructor (props) {
        super(props);
        this.state = {
            getProductDetail: [],
        }
        let id = this.props.match.params.id;
        this.addProductIntoCart = this.addProductIntoCart.bind(this);

        axios.get(Environment.apiurl+'/product/details/'+id)
        .then(result => {
            if(result){
                console.log(result.data);
                this.setState({getProductDetail: result.data.response});
            }
        });
    }

    setvalueInstate= (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    addProductIntoCart(e, param, param1, param2, param3, param4) {
        e.preventDefault();
        alert(e.target.quantity.value); 
        console.log(e.target.quantity.value);
        var cartData =
        {
            "product_id":param,
            "category_id":param1,
            "sub_category_id":param2,
            "price":param3,
            "user_id":param4,
            // "quantity":param5,
        };

        axios.post(Environment.apiurl+"/addProductIntoCart", cartData).then(result => {
            if(result.data.status=="success"){
                // console.log(result.data.status);
                Alert.success(result.data.message, {
                    position: 'bottom-right',
                    timeout: 3000
                });
            }else{
                Alert.error(result.data.message, {
                    position: 'bottom-right',
                    timeout: 3000
                });
            }
        });
    }

    render() {
        const options1 = {
            items: 1,
            // dots:false,
            callbacks:true,
            // nav:true,
            loop:true,
            autoplay: true,
            autoplayTimeout:3500,
            smartSpeed:800,
            singleItem:true,
            // navText: ["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"],
            responsiveClass:true,    
        };

        const options2 = {
            margin:10,
            items: 4,
            dots:false,
            callbacks:true,
            // nav:true,
            loop:true,
            autoplay: true,
            autoplayTimeout:3500,
            smartSpeed:800,
            singleItem:true,
            // navText: ["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"],
            responsiveClass:true,    
        };

        return (
            <div>
                <Header />

                <section class="pageBanner">
                    <figure><img src={Environment.apiurl+"/images/page-banner.jpg"} class="img-fluid" /></figure>
                    <div class="overLay_1">
                        <div class="container">
                            <div class="row">
                                <div class="col-sm-12 text-center">
                                    <h1>Shop</h1>
                                    <ol class="breadcrumb">
                                        <li><a href={Environment.webrl}>Home</a></li>
                                        <li class="active">Product Details</li>
                                    </ol>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

                <If condition={this.state.getProductDetail.length > 0}>
                    <Then>
                    {this.state.getProductDetail.map((item, index) =>    
                    <div>                
                    <section class="listing">
                        <div class="container">
                            <div class="row">
                            <div class="col-md-5 thumSlider">

                            <OwlCarousel id="sync1" class="owl-carousel owl-theme"  options={options1}>
                                    <div class="item"><img src={Environment.apiurl+"/images/big1.jpg"} class="img-fluid" /></div>
                                    <div class="item"><img src={Environment.apiurl+"/images/big2.jpg"} class="img-fluid" /></div>
