import React, { Component } from 'react';
import {Environment} from '../components/environment/index';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import OwlCarousel from 'react-owl-carousel2';

class TodayDeal extends Component {

    constructor (props){
        super(props);

        this.state = {
            todayDealList: [],
        }

        axios.post(Environment.apiurl+'/getTodaysDealList')
            .then(result => {
                if(result){
                    // console.log(result);
                    this.setState({todayDealList: result.data.response});
                }
            });
    }

    render(){
        const options = {
            items: 4,
            nav: true,
            rewind: true,
            autoplay: false,
            dots: false,
            navText: ["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"],
        };

        return (
            <div>
                <section class="products">
                    <div class="container">
                        <h2>Todays's Deal</h2>

                        {this.state.todayDealList.length>0?
                        <OwlCarousel ref="car" options={options} >                           
                            {this.state.todayDealList.map((item, index) =>
                            <div class="item">
                                <div class="itemBox">
                                    <figure>
                                        <a href={Environment.weburl+'/product/details/'+item.id}><img src={Environment.apiurl+'/products/'+item.image} class="img-fluid" /></a>
                                    </figure>
                                    <div class="addCart"> 
                                        <a href="#" onClick={(e) => this.addProductIntoWishlist(item.id)} class="wishList"><i class="fas fa-heart"></i></a>
                                        <button onClick={(e) => this.addProductIntoCart(item.id)}>Add to cart</button>
                                        <a href="#" class="comPare"><i class="fas fa-info-circle"></i></a> 
                                    </div>
                                    <h3><a href={Environment.weburl+'/product/details/'+item.id}>{item.product_name}</a></h3>
                                    <div class="prPrice">${item.price}</div>
                                </div>
                            </div>
                            )}
                        </OwlCarousel> 
                        :""} 

                    </div>
                </section>
            </div>
        )
    }
}

export default TodayDeal;
