import React, { Component } from 'react';
import {Environment} from '../components/environment/index';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import OwlCarousel from 'react-owl-carousel2';

class TodayDeal extends Component {

    constructor (props){
        super(props);

        this.state = {
            todayDealList: [],
        }

        axios.post(Environment.apiurl+'/getTodaysDealList')
            .then(result => {
                if(result){
                    // console.log(result);
                    this.setState({todayDealList: result.data.response});
                }
            });
    }

    render(){
        const options = {
            items: 6,
            nav: false,
            rewind: true,
            autoplay: true,
            dots: false,
        };

        return (
            <div>
                <section class="products">
                    <div class="container">
                        <h2>Todays's Deal</h2>
                        {/* <div id="products1" class="owl-carousel owl-theme"> */}
                        <OwlCarousel ref="car" options={options} >
                            <If condition={this.state.todayDealList.length > 0 }>
                                <Then>
                                    {this.state.todayDealList.map((item, index) =>
                                    <div class="item">
                                        <div class="itemBox">
                                            <figure>
                                                <a href="#"><img src={Environment.apiurl+'/products/'+item.image} class="img-fluid" /></a>
                                            </figure>
                                            <div class="addCart"> 
                                                <a href="#" class="wishList"><i class="fas fa-heart"></i></a>
                                                <button>Add to cart</button>
                                                <a href="#" class="comPare"><i class="fas fa-info-circle"></i></a> 
                                            </div>
                                            <h3><a href="#">{item.product_name}</a></h3>
                                            <div class="prPrice">${item.price}</div>
                                        </div>
                                    </div>
                                    )}
                                </Then>
                            </If> 
                        </OwlCarousel>   
                        {/* </div> */}
                    </div>
                </section>
            </div>
        )
    }
}

export default TodayDeal;
