import React, { Component } from 'react';
import {Environment} from '../components/environment/index';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import OwlCarousel from 'react-owl-carousel2';

class TodayDeal extends Component {

    constructor (props){
        super(props);

        this.state = {
            todayDealList: [],
        }

        axios.post(Environment.apiurl+'/getTodaysDealList')
            .then(result => {
                if(result){
                    console.log(result);
                    this.setState({todayDealList: result.data.response});
                }
            });
    }


    addProductIntoCart(param, param1, param2, param3, param4, param5) {
        var cartData =
        {
            "product_id":param,
            "category_id":param1,
            "sub_category_id":param2,
            "price":param3,
            "quantity":param4,
            "user_id":param5,
        };

        axios.post(Environment.apiurl+"/addProductIntoCart", cartData).then(result => {
            if(result.data.status=="true"){
                console.log(result.data);
                this.setState({getCartStatus: result.data.response});
            }else{
                this.setState({getCartStatus:[]});
                console.log("get error from data");
            }
        });
    }

    render(){
        const options = {
            // items: 4,
            responsiveClass:true,
            responsive:{
                0:{
                    items:2,
                    nav:true
                },
                600:{
                    items:3,
                    nav:false
                },
                1000:{
                    items:4,
                    nav:true,
                    loop:false
                }
            },
            nav: true,
            rewind: true,
            autoplay: false,
            dots: false,
            navText: ["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"],
        };

        return (
            <div>
                <section class="products">
                    <div class="container">
                        <h2>Todays's Deal</h2>

                        {this.state.todayDealList.length>0?
                        <OwlCarousel id="products1" class="owl-carousel owl-theme" options={options} >
                        {/* <div id="products1" class="owl-carousel owl-theme"> */}
                            {this.state.todayDealList.map((item, index) =>
                            <div class="item">
                                <div class="itemBox">
                                    <figure>
                                        <a href={Environment.weburl+'/product/details/'+item.id}><img src={Environment.apiurl+'/products/'+item.image} class="img-fluid" /></a>
                                    </figure>


                                    <If condition={localStorage.getItem("email")}>
                                        <Then>
                                            <div class="addCart"> 
                                                <a href="#" onClick={(e) => this.addProductIntoWishlist(item.id, item.category_id, item.sub_category_id)} class="wishList"><i class="fas fa-heart"></i></a>
                                                <button onClick={(e) => this.addProductIntoCart(item.id, item.category_id, item.sub_category_id, item.price, 1, session_user_id)}>Add to cart</button>
                                                <a href="#" class="comPare"><i class="fas fa-info-circle"></i></a> 
                                            </div>
                                        </Then>
                                        <Else>
                                            <div class="addCart"> 
                                                <a href="#" data-toggle="modal" data-target="#loginModal" class="wishList"><i class="fas fa-heart"></i></a>
                                                <button data-toggle="modal" data-target="#loginModal">Add to cart</button>
                                                <a href="#" class="comPare"><i class="fas fa-info-circle"></i></a> 
                                            </div>
                                        </Else>
                                    </If>


                                    <h3><a href={Environment.weburl+'/product/details/'+item.id}>{item.product_name}</a></h3>
                                    <div class="prPrice">${item.price}</div>
                                </div>
                            </div>
                            )}
                            {/* </div> */}
                        </OwlCarousel> 
                        :""}
                    </div>
                </section>
            </div>
        )
    }
}

export default TodayDeal;
