import React, { Component } from 'react';
import {Environment} from '../components/environment/index';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';

class ChooseUs extends Component {

    constructor (props){
        super(props);

        this.state = {
            staticContentList: [],
        }

        axios.post(Environment.apiurl+'/whyChooseUs')
            .then(result => {
                if(result){
                    // console.log(result);
                    this.setState({staticContentList: result.data.response});
                }
            });
    }

    render(){
        return (
            <div>
                <If condition={this.state.getSubCategoryList.length > 0}>
                    <Then>
                    {this.state.getSubCategoryList.map((item, index) =>
                        <li><a href="javaScript:void(0);" onClick={(e) => this.getFilterCategoryProductList(item.id)}><i class="fas fa-chevron-right"></i>{ item.sub_category_name }</a></li>
                    )}
                    </Then>
                </If>


                <If condition = {this.state.staticContentList.length > 0}>
                </If>




                <section class="whyUs">
                    <div class="container">
                        <div class="whIn">
                            <div class="col-lg-7">
                                <h2>Why Choose Us</h2>
                                <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</p>
                                <a href="#">Read More</a> 
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}

export default ChooseUs;
