    //         if(result.data.status==="success"){
    //             // window.location.href=Environment.weburl;
    //             // window.location.replace(Environment.weburl);
    //         }
    //     })
    // }

    subscribeUser(){
        alert(this.state.email);
        var email = this.state.email;
    }

    render(){
        return (
            <div>
                <section class="newsLetter">
                    <div class="container">
                        <div class="letterBox">
                            <div class="row">
                                <aside class="col-lg-4">
                                    <ul>
                                        <li><a href="#"><i class="fab fa-facebook-f"></i></a></li>
                                        <li><a href="#"><i class="fab fa-twitter"></i></a></li>
                                        <li><a href="#"><i class="fab fa-google-plus-g"></i></a></li>
                                        <li><a href="#"><i class="fab fa-instagram"></i></a></li>
                                        <li><a href="#"><i class="fab fa-youtube"></i></a></li>
                                    </ul>
                                </aside>
                                <aside class="col-lg-8">
                                    <label>Sign up for newsletter</label>
                                    {/* <form onSubmit={this.subscribeUser.bind(this)} method="post">
                                        <div class="inpBox">
                                            <input name="email" type="email" class="inpTxt" placeholder="Your email address..." />
                                            <input name="" type="submit" value="Subscribe" class="inpBtn"/>
                                        </div>
                                    </form> */}
                                    <div class="inpBox">
                                        <input name="email" type="email" class="inpTxt" placeholder="Your email address..." value=""/>
                                        <button class="cartBtn" onClick={(e) => this.subscribeUser()} class="inpBtn">Add to Cart</button>
                                    </div>
                                </aside>
                            </div>
                        </div>
                        <Alert stack={true} timeout={3000} position='bottom-right' />
                    </div>
                </section>
            </div>
        )
    }
}

export default Newsletter;
