import React, { Component } from 'react';
import { Environment } from '../../../src/components/environment';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import Header from '../../../src/layouts/header';
import UserLeftMenu from '../../../src/layouts/user_left_menu';
import Footer from '../../../src/layouts/footer';



class Wishlist extends Component{
    constructor (props) {
        super(props);
        this.state = {
            user_id_send :'',
            wishlistProduct: [],
        }

        var user_id_send = localStorage.getItem("user_id")?localStorage.getItem("user_id"):"";

        axios.post(Environment.apiurl+'/getUserWishlistProduct', {user_id:user_id_send})
        .then(result => {
            if(result){
                // console.log(result);
                this.setState({user_id_send:user_id_send});
                this.setState({wishlistProduct: result.data.response});
            }
        });
    }

    deleteItem(param){
        alert(this.state.user_id_send);
    }

    render() {
        return (
            <div>
                <Header />
                <section class="dashBoard">
                    <div class="container-fluid">
                        <div class="row">

                            <UserLeftMenu />

                            <aside class="col-lg-9 col-sm-8">
                                <div class="dashRight">
                                    <ul class="breadcrumb">
                                        <li><a href="#">My Profile</a></li>
                                        <li class="active">My Wishlist</li>
                                    </ul>
                                    <div class="table-responsive">
                                        <ul class="orderList">
                                        {this.state.wishlistProduct.length>0}
                                        {this.state.wishlistProduct.map( (item, index) =>
                                            <li>                                    
                                                <div class="orderTable">
                                                    <div class="table-responsive">
                                                        <table>
                                                            <tr>
                                                                <td><a href={Environment.weburl+"/product/details/"+item.id}><img src={Environment.apiurl+'/products/'+item.image} class="img-fluid" alt="" /></a></td>
                                                                <td><a href={Environment.weburl+"/product/details/"+item.id}>{item.product_name}</a></td>
                                                                <td align="center">${item.price}</td>
                                                                <td align="center"><a href="#" onClick={e => window.confirm("Are you sure you wish to delete this item?") && this.deleteItem(item.id) }class="remove"><i class="fas fa-trash-alt"></i></a></td>
                                                            </tr>
                                                        </table>
                                                    </div>
                                                </div>
                                            </li>
                                        )}
                                        </ul>
                                    </div>
                                </div>
                            </aside>
                        </div>
                    </div>
                </section>
                <Footer />
            </div>
        )
    }    

}

export default Wishlist;
