import React, { Component } from 'react';
import {Environment} from '../components/environment/index';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';

class ChangePasswordPopup extends Component {

    constructor (props){
        super(props);

        // this.state = {
        //     information: [],
        //     quickLinks: [],
        //     gallery: [],
        //     weather: [],
        // }

        // axios.get(Environment.apiurl+'getInformation')
        //     .then(result => {
        //         if(result){
        //             this.setState({information: result.data.response});
        //         }
        //     });
    }


    changePassword(e){
        e.preventDefault();
        this.setState({
          loading: true
        });

        axios.post(Environment.apiurl+"/login",{
            email:e.target.email.value,
            password: e.target.password.value
        })
        .then(result => {
            // console.log(result.data);
            if(result.data.status==="success"){
                var data=result.data.data;
                localStorage.setItem("user_id", data.id);
                localStorage.setItem("email", data.email);
                localStorage.setItem("first_name", data.first_name);
                localStorage.setItem("flast_name", data.last_name);
                localStorage.setItem("address", data.address_1);
                localStorage.setItem("phone_no", data.mobile);
                localStorage.setItem("profile_image", data.profile_image);
                // localStorage.setItem("token",data.token);
                document.getElementById('loginModal').click();
                this.setState({sucessMsg2: result.data.message});
                localStorage.setItem("sucessMsg", result.data.message);
                window.location.reload();
            }else{
                this.setState({errorMsg2: result.data.message});
            }
        })
        // .catch(error => {
        //     //errorMsg=error.response.data.message;
        //     this.setState({errorMsg: error.result.data.message});
        //     //console.log(error.response.data.message);
        // });
    }

    render(){
        return (
            <div>
                <div class="modal popUp popUp1" id="pwd">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-body">
                                <button type="button" class="close" data-dismiss="modal">&times;</button>
                                <div class="mdLeft">
                                    <div class="popupOverlay">
                                        <h3>Change Password</h3>
                                    </div>
                                </div>
                                <div class="mdRight">
                                    <div class="formBlock">
                                    <form onSubmit={this.changePassword.bind(this)} method="post">
                                        <div class="form-group">
                                            <label>Current Password <span>*</span></label>
                                            <input name="" type="password" class="inpField" placeholder="password" required />
                                        </div>
                                        <div class="form-group">
                                            <label>New Password <span>*</span></label>
                                            <input name="" type="password" class="inpField" placeholder="password" required />
                                        </div>
                                        <div class="form-group">
                                            <label>Confirm Password <span>*</span></label>
                                            <input name="" type="password" class="inpField" placeholder="password" required />
                                        </div>
                                        <div class="row btnRow">
                                            <aside class="col-sm-6">
                                            <button type="submit" class="subMit">Submit</button>
                                            </aside>
                                        </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default ChangePasswordPopup;
