import React, { Component } from 'react';
import { Environment } from '../../../src/components/environment';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import Header from '../../../src/layouts/header';
import Footer from '../../../src/layouts/footer';
import { strict } from 'assert';
// import CategoryFilter from '../../../src/layouts/filter';

class CategoryProduct extends Component{
    constructor (props) {
        super(props);
        this.state = {
            getProductList: [],
            getSubCategoryList: [],
            slugValue: '',

        }
        let slug = this.props.match.params.slug;
        this.getFilterProduct=this.getFilterProduct.bind(this);

        // if(slug){
            axios.get(Environment.apiurl+'/product/'+slug)
            .then(result => {
                if(result){
                    // console.log(result);
                    this.setState({getProductList: result.data.response});
                    this.setState({slugValue: this.props.match.params.slug});


                }
            });
        // }
        // else {
        //     axios.get(Environment.apiurl+'/product')
        //     .then(result => {
        //         if(result){
        //             // console.log(result);
        //             this.setState({getProductList: result.data.response});
        //         }
        //     });
        // }


        axios.get(Environment.apiurl+'/getAllSubCategoryList/'+slug)
        .then(result => {
            if(result){
                // console.log(result.data);
                this.setState({getSubCategoryList: result.data.response});
            }
        });
        console.log(this.state.slugValue);
    }

    getFilterProduct(param) {
        // alert(this.state.slugValue);
        var filterData =
        {
            "slug":this.state.slugValue,
            "subCategoryId":param,
        };

        axios.post(Environment.apiurl+"getFilterProductList").then(result => {
            if(result.data.status=="true"){
                this.setState({productsListData: result.data.response});
            }else{
                this.setState({productsListData:[]});
                console.log("get error from data");
            }
        });
    }

    render() {
        return (
            <div>
                <Header />
                <section class="pageBanner">
                    <figure><img src={Environment.apiurl+"/images/page-banner.jpg"} class="img-fluid" /></figure>
                    <div class="overLay_1">
                        <div class="container">
                            <div class="row">
                                <div class="col-sm-12 text-center">
                                    <h1>Shop</h1>
                                    <ol class="breadcrumb">
                                        <li><a href={Environment.weburl}>Home</a></li>
                                        <li class="active">Shop</li>
                                    </ol>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

                <section class="listing">
                    <div class="container">
                        <div class="row">

                        <div class="col-md-4 col-lg-3">
                        <button class="open-mypage2">Categories</button>
                            <div id="mypage-info2" class="slideForm"> 
                                <a href="#" class="up"><i class="fa fa-chevron-up" aria-hidden="true"></i></a>
                                <div class="scrollBar" data-simplebar>
                                    <div class="bs-example">
                                    <h3 class="shopTitle">Shop by category</h3>
                                        <div class="accordion" id="accordionExample">
                                            <div class="card">
                                                <ul class="subMenu">
                                                    <If condition={this.state.getSubCategoryList.length > 0}>
                                                        <Then>
                                                        {this.state.getSubCategoryList.map((item, index) =>
                                                            <li><a href="javaScript:void(0);" onClick={(e) => this.getFilterProduct(item.id)}><i class="fas fa-chevron-right"></i>{ item.sub_category_name }</a></li>
                                                        )}
                                                        </Then>
                                                    </If>
                                                </ul>
                                            </div>                                           
                                        </div>
                                    </div>

                                    <div class="priceFilter">
                                    <h3 class="shopTitle">Filter by price</h3>
                                        <div class="filterimg">
                                            <div id="slider-range" class="price-filter-range" name="rangeInput">
                                            </div>
                                            <div class="priceValue">
                                                <p>
                                                    <button>Filter</button>
                                                </p>
                                                <h3>Price:</h3>
                                            <input type="" min="0" max="9900" oninput="validity.valid||(value='1');" id="min_price" class="price-range-field" />
                                            -
                                            <input type="" min="0" max="10000" oninput="validity.valid||(value='10000');" id="max_price" class="price-range-field" />
                                            </div>
                                        </div>
                                    </div>

                                </div>
                            </div>
                        </div>


                        <div class="col-md-8 col-lg-9">
                            <div class="showMore">
                                <h3>Showing 1â€“12 of 16 results</h3>
                                <div class="sortBy">
                                    <select name="">
                                    <option>Default sorting</option>
                                    </select>
                                </div>
                            </div>
                            <div class="clear"></div>

                                <div class="products">
                                    <div class="row">
                                        <If condition={this.state.getProductList.length > 0}>
                                            <Then>
                                                {this.state.getProductList.map((item, index) => 
                                                <div class="col-6 col-md-6 col-lg-4">
                                                    <div class="itemBox">
                                                        <figure>
                                                            <a href={Environment.weburl+"/product/details/"+item.id}><img src={Environment.apiurl+'/products/'+item.image} class="img-fluid" /></a>
                                                        </figure>
                                                        <div class="addCart"> 
                                                            <a href="#" class="wishList"><i class="fas fa-heart"></i></a>
                                                            <button>Add to cart</button>
                                                            <a href="#" class="comPare"><i class="fas fa-info-circle"></i></a> 
                                                        </div>
                                                        <h3>
                                                            <a href={Environment.weburl+"/product/details/"+item.id}>{item.product_name}</a>
                                                        </h3>
                                                        <div class="prPrice">${item.price}</div>
                                                    </div>
                                                </div>
                                                )}
                                            </Then>
                                        </If>
                                    </div>
                                </div>

                                <div class="paginate">
                                    <ul class="pagination">
                                    <li><a href="#" class=" "><i class="fas fa-long-arrow-alt-left"></i></a></li>
                                    <li class="active "><span>1</span></li>
                                    <li><a href="#" class=" ">2</a></li>
                                    <li><a href="#" class=" ">3</a></li>
                                    <li><a href="#" class=" "><i class="fas fa-long-arrow-alt-right"></i></a></li>
                                    </ul>
                                    <div class="clearfix"></div>
                                </div>
                                <div class="clear"></div>
                            </div>


                        </div>
                    </div>
                </section>
                <Footer />
            </div>
        )
    }    

}

export default CategoryProduct;
