import React, { Component } from 'react';
import {Environment} from '../components/environment/index';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';

class Banner extends Component {

    constructor (props){
        super(props);

        this.state = {
            bannerList: [],
        //     quickLinks: [],
        //     gallery: [],
        //     weather: [],
        }

        console.log(Environment.apiurl);
        axios.post(Environment.apiurl+'/getBannerList')
            .then(result => {
                if(result){
                    console.log(result);
                    this.setState({bannerList: result.data.response});
                }
            });
    }

    render(){
        return (
            <div>
                <section class="slider">
                    <div id="slide" class="owl-carousel owl-theme">
                        <If condition={this.state.bannerList.length > 0 }>
                            <Then>
                                {this.state.bannerList.map((item, index) =>
                                <div class="item">
                                    <div class="slideCaption">
                                        <div class="container">
                                            <div class="row">
                                                <aside class="col-lg-5 col-sm-7">
                                                <h1>{item.title}</h1>
                                                <p>Auto Detailing Supplies Superstore</p>
                                                <a href="#">Read More</a> </aside>
                                                <aside class="col-lg-7 col-sm-5"> <img src={Environment.apiurl+'/banners/'+item.image} class="img-fluid" /> </aside>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                )}
                             </Then>
                         </If>
                        {/* <div class="slideCaption">
                            <div class="container">
                            <div class="row">
                                <aside class="col-lg-5 col-sm-7">
                                <h1>Auto Detailing Supplies Superstore</h1>
                                <p>A community of people, who care about cars</p>
                                <a href="#">Read More</a> </aside>
                                <aside class="col-lg-7 col-sm-5"> <img src="images/slide-product1.png" class="img-fluid"> </aside>
                            </div>
                            </div>
                        </div> */}
                    </div>
                </section>
            </div>
        )
    }
}

export default Banner;
