import React, { Component } from 'react';
import {Environment} from '../components/environment/index';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';

class ClientTestimonial extends Component {

    constructor (props){
        super(props);

        this.state = {
            testimonialData: [],
        }

        axios.post(Environment.apiurl+'/clientTestimonial')
            .then(result => {
                if(result){
                    console.log(result);
                    this.setState({testimonialData: result.data.response});
                }
            });
    }

    render(){
        return (
            <div>
                <section class="clientWords">
                <figure>
                    <img src={Environment.apiurl+"/images/testi-image.jpg"} class="img-fluid" />
                </figure>
                    <div class="container">
                        <div class="testimonials">
                            <h2>Client Says</h2>
                            <div id="testi" class="owl-carousel owl-theme">

                                <div class="item">
                                    <div class="comments">
                                        <div class="pic">
                                            <img src={Environment.apiurl+"/images/client-img.jpg"} />
                                        </div>
                                        <h3>John B. Walker <span>Lawyer, New York</span></h3>
                                        <p>I am grateful to the professional technical support team who helped me with choosing and installing multiple products throughout the years of my ordering from the company. I like the service very much, and hope you continue making your customers happy and satisfied!</p>
                                    </div>
                                </div>



                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}

export default ClientTestimonial;
