import React, { Component } from 'react';
import { Environment } from '../../../src/components/environment';
import axios from 'axios';
import { If, Then, Else, Switch, Case} from 'react-if';
import Header from '../../../src/layouts/header';
import Footer from '../../../src/layouts/footer';

class Cart extends Component{
    constructor (props) {
        super(props);
        this.state = {
            user_id_send :'',
            cartlistProduct : [],
            quantity : 0,
        }
        var user_id_send = localStorage.getItem("user_id")?localStorage.getItem("user_id"):"";

        this.addQuantity = this.addQuantity.bind(this);
        this.removeQuantity = this.removeQuantity.bind(this);

        axios.post(Environment.apiurl+'/getUserCartlistProduct', {user_id:user_id_send})
        .then(result => {
            if(result){
                this.setState({user_id_send:user_id_send});
                this.setState({cartlistProduct: result.data.response});
            }
        });
    }

    addQuantity(e){
        alert(e);
        this.setState({ quantity: this.state.quantity+'_'+e+1 });
    }
    removeQuantity(e){
        if(this.state.quantity>0){
            this.setState({ quantity: this.state.quantity-1 });
        }
    }

    deleteCartlistItem(param){
        var wishlistItem =
        {
            "id":param,
            "user_id":this.state.user_id_send,
        };

        axios.post(Environment.apiurl+"/deleteCartlistItem", wishlistItem).then(result => {
            if(result.data.status=="success"){
                this.setState({sucessMsg: result.data.message});
                localStorage.setItem("sucessMsg", result.data.message);
            } else {
                this.setState({sucessMsg3: result.data.message});
                localStorage.setItem("sucessMsg", result.data.message);
            }
            setTimeout(function () {
                window.location.reload(1);
            }, 1000);
        });
    }


    render() {
        return (
            <div>
                <Header />
                <section class="cartPage">
                    <div class="container">
                        <div class="row">
                            <div class="col-12">
                                <h2>Cart</h2>
                                <ul class="breadcrumb">
                                    <li><a href="#">Home</a></li>
                                    <li>Cart</li>
                                </ul>
                            </div>

                            <div class="col-12">
                            <If condition={this.state.sucessMsg}>
                                <Then>
                                    <div className="alert alert-success">{this.state.sucessMsg}</div>
                                </Then>
                            </If>

                            <If condition={this.state.errorMsg}>     
                                <Then>
                                    <div className="alert alert-danger">{this.state.errorMsg}</div>
                                </Then>
                            </If>
                                <div class="table-responsive">
                                <table class="table crTable">
                                    <thead>
                                        <tr>
                                            <th>Product</th>
                                            <th>Price</th>
                                            <th>Quantity</th>
                                            <th colspan="2">Total</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                    {this.state.cartlistProduct.length>0}
                                    {this.state.cartlistProduct.map( (item, index) =>
                                    <tr>
                                        <td data-label="Product"><figure class="prodImg"><img src={Environment.apiurl+'/products/'+item.image} /></figure> <h4>{item.product_name}</h4></td>
                                        <td data-label="Price">${item.price}</td>
                                        <td data-label="Quantity">
                                            <div class="plusMinus prodCount">
                                                <form id='myform' method='POST' action='#'>
                                                    <input type='button' value='-' class='numb' field='quantity' onClick={(e) => this.removeQuantity(item.id)}/>

                                                    <input type='text' name='quantity' id={'quantity_'+item.id} value={this.state.quantity+'_'+item.id} class='qty numb' />

                                                    <input type='button' value='+' class='numb' field='quantity' onClick={(e) => this.addQuantity(item.id)}/>
                                                </form>
                                            </div>
                                        </td>
                                        <td data-label="Total"> ${item.total_amount}</td>
                                        <td class="text-center"><a href="javascript:void(0);" onClick={e => window.confirm("Are you sure you wish to delete this item?") && this.deleteCartlistItem(item.cart_id) }><img src={Environment.apiurl+"/images/cross.png"} /></a></td>
                                    </tr>
                                    )}

                                    </tbody>
                                </table>
                                </div>
                                <If condition={this.state.cartlistProduct.length>0}>
                                <div class="contnuShop">
                                    <div class="row">
                                        <div class="col-6">
                                            <button class="btn gray">Continue shopping</button>
                                        </div>
                                        <div class="col-6 text-right">
                                            <button class="btn red">Update cart</button>
                                        </div>
                                    </div>
                                </div>
                                </If>

                                <If condition={this.state.cartlistProduct.length>0}>
                                <div class="carTotal">
                                    <div class="row">
                                        <div class="col-lg-3">
                                            <h3>Cart Totals</h3>
                                        </div>
                                        <div class="col-lg-9">
                                            <ul class="cartVelu">
                                                <li><h3>Subtotal</h3></li>
                                                <li>$310.00</li>
                                            </ul>

                                            <ul class="cartVelu">
                                                <li><h3>Shipping</h3></li>
                                                <li>
                                                    <label><input type="radio" value="radio" name="radio1" /> Self Pick-Up (Free)</label>
                                                    <label><input type="radio" value="radio" name="radio1" /> Standard Delivery ($15)</label>
                                                </li>
                                            </ul>

                                            <div class="cartAddrs">
                                                <h3>Select Address</h3>
                                                <ul class="optAddress">
                                                    <li>
                                                        <label><input type="radio" value="radio" name="radio2" />Work</label>
                                                        <p>810 Carriage Drive Kissimmee, FL 34741</p>
                                                    </li>
                                                    <li>
                                                        <label><input type="radio" value="radio" name="radio2" />Office</label>
                                                        <p>810 Carriage Drive Kissimmee, FL 34741</p>
                                                    </li>
                                                    <li>
                                                        <a class="addNew" href="#">Add New Address</a>
                                                    </li>
                                                </ul>
                                            </div>

                                            <ul class="cartVelu">
                                                <li><h3>Total</h3></li>
                                                <li>$325.00</li>
                                            </ul>                                            
                                        </div>
                                    </div>
                                </div>
                                </If>

                                <div class="clearfix"></div>
                                <button class="checkoutBtn">Proceed to Checkout</button>



                            </div>

                        </div>
                    </div>
                </section>
                <Footer />
            </div>
        )
    }    

}

export default Cart;
